class scriptAttribute {
	private int index;
	private float value;

	public scriptAttribute() {
		this.index = 0;
		this.value = 0f;
	}

	public scriptAttribute(int index, float value) {
		this.index = index;
		this.value = value;
	}

	public int getIndex() {
		return index;
	}
	public void setIndex(int index) {
		this.index = index;
	}
	public float getValue() {
		return value;
	}
	public void setValue(float value) {
		this.value = value;
	}
}

class scriptItem {
	private int index;
	private String classname;
	private String slot;
	private int quality;
	private int level;
	private Vector<scriptAttribute> attributes;

	public scriptItem() {
		this.index = 0;
		this.classname = "";
		this.slot = "";
		this.quality = 0;
		this.level = 0;
		this.attributes = new Vector<scriptAttribute>(16);
	}

	public scriptItem(int index, String classname, String slot, int quality,
			int level) {
		this.index = index;
		this.classname = classname;
		this.slot = slot;
		this.quality = quality;
		this.level = level;
		this.attributes = new Vector<scriptAttribute>(16);
	}

	public scriptItem(int index, String classname, String slot, int quality,
			int level, Vector<scriptAttribute> attributes) {
		this.index = index;
		this.classname = classname;
		this.slot = slot;
		this.quality = quality;
		this.level = level;
		this.attributes = attributes;
	}

	public int getIndex() {
		return index;
	}
	public void setIndex(int index) {
		this.index = index;
	}
	public String getClassname() {
		return classname;
	}
	public void setClassname(String classname) {
		this.classname = classname;
	}
	public String getSlot() {
		return slot;
	}
	public void setSlot(String string) {
		this.slot = string;
	}
	public int getQuality() {
		return quality;
	}
	public void setQuality(int i) {
		this.quality = i;
	}
	public int getLevel() {
		return level;
	}
	public void setLevel(int level) {
		this.level = level;
	}
	public Vector<scriptAttribute> getAttributes() {
		return attributes;
	}
	public void setAttributes(Vector<scriptAttribute> attributes) {
		this.attributes = attributes;
	}
}

